window.addEventListener("touchend",function(a){w&&a.changedTouches[0].pageX-z>threshold?open():w&&close();w=x=!1},!1);$(drawerIcon).click(function(a){v?close():open()});function close(){v=!1;$(navigation).animate({left:"0"})}function open(){v=!0;$(navigation).animate({left:width+"px"})};
